import React, { useState } from "react";
import axios from "axios";

export default function Dictionary() {
  const [word, setWord] = useState("");
  const [defi, setDefi] = useState({});

  function handleKeywordChange(event) {
    setWord(event.target.value);
  }

  function showDefi(response) {
    setDefi({
      word: response.data[0].word,
      pronounce: response.data[0].phonetics[0].text,
      definition: response.data[0].meanings[0].definitions,
    });
  }

  function submit(event) {
    event.preventDefault();
    axios
      .get(`https://api.dictionaryapi.dev/api/v2/entries/en_US/${word}`)
      .then(showDefi);
  }

  function Definition() {
    if (Object.keys(defi).length !== 0) {
      return (
        <div className="definition">
          <h2>{defi.word}</h2>
          <h3>{defi.pronounce}</h3>
          <p>Definition: {defi.definition}</p>
        </div>
      );
    }
    return <h3>Type a word</h3>;
  }

  return (
    <div className="Dictionary">
      <form onSubmit={submit}>
        <input type="search" onChange={handleKeywordChange} />
        <input type="submit" className="submitbtn" />
      </form>
      <Definition />
    </div>
  );
}
